{"command":["bash","-lc","python - <<'PY'
import json, os, requests
BASE = "http://localhost:3005/api"
API_KEY = "mb_pcQTYXPzL/epq2cszKhBegdr09zM82EiDAjZFlBNa1E="
HEADERS = {"X-API-Key": API_KEY, "Content-Type": "application/json"}

# Helpers
def api_get(path):
    resp = requests.get(f"{BASE}{path}", headers=HEADERS)
    resp.raise_for_status()
    return resp.json()

def api_post(path, payload):
    resp = requests.post(f"{BASE}{path}", headers=HEADERS, data=json.dumps(payload))
    resp.raise_for_status()
    return resp.json()

def api_put(path, payload):
    resp = requests.put(f"{BASE}{path}", headers=HEADERS, data=json.dumps(payload))
    resp.raise_for_status()
    return resp.json()

# 1. Database lookup
dbs = api_get('/database')['data']
if not dbs:
    raise SystemExit('No databases configured in Metabase.')
DB_ID = None
for db in dbs:
    details = db.get('details') or {}
    if details.get('dbname') == 'epb' or 'epb' in db.get('name','').lower():
        DB_ID = db['id']
        db_name = db['name']
        break
if DB_ID is None:
    DB_ID = dbs[0]['id']
    db_name = dbs[0].get('name', 'unknown')
print(f"Using database id {DB_ID} ({db_name}).")

# 2. Ensure collection exists
tree = api_get('/collection/tree')
COLLECTION_ID = None
for node in tree:
    if node.get('name') == 'Sales Insights':
        COLLECTION_ID = node['id']
        break
if COLLECTION_ID is None:
    payload = {"name": "Sales Insights", "color": "#509EE3", "description": "Automated import"}
    COLLECTION_ID = api_post('/collection', payload)['id']
    print(f"Created collection 'Sales Insights' (id={COLLECTION_ID}).")
else:
    print(f"Using existing collection 'Sales Insights' (id={COLLECTION_ID}).")

items_payload = api_get(f'/collection/{COLLECTION_ID}/items')
existing_cards = {item['name']: item['id'] for item in items_payload.get('data', []) if item.get('model') == 'card'}
print(f"Existing cards in collection: {list(existing_cards.keys())}")

# 3. Upsert cards
def upsert_card(card):
    dq = card['dataset_query']
    dq = {**dq, 'database': DB_ID}
    if 'native' in dq and 'template-tags' not in dq['native']:
        dq['native']['template-tags'] = {}
    payload = {
        'name': card['name'],
        'dataset_query': dq,
        'display': card.get('visualization', 'table'),
        'collection_id': COLLECTION_ID,
        'description': card.get('description', ''),
        'visualization_settings': {}
    }
    name = payload['name']
    if name in existing_cards:
        card_id = existing_cards[name]
        payload['id'] = card_id
        api_put(f'/card/{card_id}', payload)
        print(f"Updated card '{name}' (id={card_id}).")
        return card_id
    else:
        card_id = api_post('/card', payload)['id']
        existing_cards[name] = card_id
        print(f"Created card '{name}' (id={card_id}).")
        return card_id

cards_created = {}
root = os.path.join('metabase','dashboards','sales-insights')
json_files = ['daily-sales-health.json','quotation-pipeline.json','quote-to-order-conversion.json']
for filename in json_files:
    with open(os.path.join(root, filename)) as f:
        data = json.load(f)
    for card in data.get('cards', []):
        card_id = upsert_card(card)
        slug = card.get('slug')
        if slug:
            cards_created[slug] = card_id

# 4. Dashboard
all_dashboards = api_get('/dashboard')
dashboard_id = None
for dash in all_dashboards:
    if dash.get('name') == 'Sales Insights':
        dashboard_id = dash['id']
        break
if dashboard_id is None:
    payload = {
        'name': 'Sales Insights',
        'description': 'UI-first dashboard for sales KPIs, pipeline, and conversion.',
        'collection_id': COLLECTION_ID,
        'parameters': []
    }
    dashboard_id = api_post('/dashboard', payload)['id']
    print(f"Created dashboard 'Sales Insights' (id={dashboard_id}).")
else:
    print(f"Updating existing dashboard 'Sales Insights' (id={dashboard_id}).")
    dash_details = api_get(f'/dashboard/{dashboard_id}')
    for dashcard in dash_details.get('ordered_cards', []):
        card_id = dashcard['id']
        requests.delete(f"{BASE}/dashboard/{dashboard_id}/dashcard/{card_id}", headers=HEADERS)

# 5. Add cards to dashboard using layout
with open(os.path.join(root, 'dashboard.json')) as f:
    dash_layout = json.load(f)

for tab in dash_layout.get('tabs', []):
    for card_info in tab.get('cards', []):
        ref = card_info['ref']
        slug = ref.split('#', 1)[1]
        card_id = cards_created.get(slug)
        if not card_id:
            print(f"Warning: No card id for slug {slug}; skipping placement.")
            continue
        layout = card_info['layout']
        payload = {
            'cardId': card_id,
            'row': layout['row'],
            'col': layout['col'],
            'sizeX': layout['size_x'],
            'sizeY': layout['size_y'],
            'parameter_mappings': []
        }
        api_post(f'/dashboard/{dashboard_id}/cards', payload)
        print(f"Placed card '{slug}' on dashboard.")

print('Metabase import complete.')
PY"],"workdir":"/Users/ivan/Project/EPB","timeout_ms":120000}

