{"command":["bash","-lc","python - <<'PY'
import json, os, requests
BASE = "http://localhost:3005/api"
API_KEY = "mb_pcQTYXPzL/epq2cszKhBegdr09zM82EiDAjZFlBNa1E="
HEADERS = {"X-API-Key": API_KEY, "Content-Type": "application/json"}

def api_get(path):
    r = requests.get(f"{BASE}{path}", headers=HEADERS)
    r.raise_for_status()
    return r.json()

def api_post(path, payload):
    r = requests.post(f"{BASE}{path}", headers=HEADERS, data=json.dumps(payload))
    r.raise_for_status()
    return r.json()

def api_put(path, payload):
    r = requests.put(f"{BASE}{path}", headers=HEADERS, data=json.dumps(payload))
    r.raise_for_status()
    return r.json()

# Database selection
dbs = api_get('/database')['data']
if not dbs:
    raise SystemExit('No databases configured.')
DB_ID = None
for db in dbs:
    details = db.get('details') or {}
    if details.get('dbname') == 'epb' or 'epb' in db.get('name','').lower():
        DB_ID = db['id']
        db_name = db['name']
        break
if DB_ID is None:
    DB_ID = dbs[0]['id']
    db_name = dbs[0].get('name', 'unknown')
print(f"Using database id {DB_ID} ({db_name}).")

# Collection
COLLECTION_ID = None
for node in api_get('/collection/tree'):
    if node.get('name') == 'Sales Insights':
        COLLECTION_ID = node['id']
        break
if COLLECTION_ID is None:
    COLLECTION_ID = api_post('/collection', {"name": "Sales Insights", "color": "#509EE3"})['id']
    print(f"Created collection 'Sales Insights' (id={COLLECTION_ID}).")
else:
    print(f"Using collection 'Sales Insights' (id={COLLECTION_ID}).")

items_payload = api_get(f'/collection/{COLLECTION_ID}/items')
existing_cards = {item['name']: item['id'] for item in items_payload.get('data', []) if item.get('model') == 'card'}

# Upsert cards
def upsert_card(card):
    dq = {**card['dataset_query'], 'database': DB_ID}
    if 'native' in dq and 'template-tags' not in dq['native']:
        dq['native']['template-tags'] = {}
    payload = {
        'name': card['name'],
        'dataset_query': dq,
        'display': card.get('visualization', 'table'),
        'collection_id': COLLECTION_ID,
        'description': card.get('description', ''),
        'visualization_settings': {}
    }
    if card['name'] in existing_cards:
        payload['id'] = existing_cards[card['name']]
        api_put(f"/card/{payload['id']}", payload)
        card_id = payload['id']
        print(f"Updated card '{card['name']}' (id={card_id}).")
    else:
        card_id = api_post('/card', payload)['id']
        existing_cards[card['name']] = card_id
        print(f"Created card '{card['name']}' (id={card_id}).")
    return card_id

root = os.path.join('metabase','dashboards','sales-insights')
json_files = ['daily-sales-health.json','quotation-pipeline.json','quote-to-order-conversion.json']
cards_by_slug = {}
for filename in json_files:
    with open(os.path.join(root, filename)) as f:
        data = json.load(f)
    for card in data.get('cards', []):
        card_id = upsert_card(card)
        if card.get('slug'):
            cards_by_slug[card['slug']] = card_id

# Dashboard
all_dashboards = api_get('/dashboard')
dashboard_id = None
for dash in all_dashboards:
    if dash.get('name') == 'Sales Insights' and dash.get('collection_id') == COLLECTION_ID:
        dashboard_id = dash['id']
        break
if dashboard_id is None:
    dashboard_id = api_post('/dashboard', {
        'name': 'Sales Insights',
        'description': 'UI-first dashboard for sales KPIs, pipeline, and conversion.',
        'collection_id': COLLECTION_ID,
        'parameters': []
    })['id']
    print(f"Created dashboard 'Sales Insights' (id={dashboard_id}).")
else:
    print(f"Using dashboard 'Sales Insights' (id={dashboard_id}).")

current_dash = api_get(f'/dashboard/{dashboard_id}')
existing_dashcards = {dc['card_id']: dc for dc in current_dash.get('dashcards', [])}

with open(os.path.join(root, 'dashboard.json')) as f:
    dash_layout = json.load(f)

dashcards_payload = []
for tab in dash_layout.get('tabs', []):
    for card in tab.get('cards', []):
        slug = card['ref'].split('#', 1)[1]
        card_id = cards_by_slug.get(slug)
        if not card_id:
            print(f"Warning: missing card for slug {slug}")
            continue
        layout = card['layout']
        base = {
            'id': existing_dashcards.get(card_id, {}).get('id', 0),
            'card_id': card_id,
            'row': layout['row'],
            'col': layout['col'],
            'size_x': layout['size_x'],
            'size_y': layout['size_y'],
            'parameter_mappings': existing_dashcards.get(card_id, {}).get('parameter_mappings', []),
            'series': existing_dashcards.get(card_id, {}).get('series', [])
        }
        dashcards_payload.append(base)

payload = {
    'name': 'Sales Insights',
    'description': 'UI-first dashboard for sales KPIs, pipeline, and conversion.',
    'collection_id': COLLECTION_ID,
    'parameters': current_dash.get('parameters', []),
    'dashcards': dashcards_payload
}
api_put(f'/dashboard/{dashboard_id}', payload)
print('Dashboard layout updated.')
PY"],"workdir":"/Users/ivan/Project/EPB","timeout_ms":120000}

