{"command":["bash","-lc","cat <<'EOF' > src/dashboard/data/metrics.ts
export type Segment = 'Enterprise' | 'Midmarket' | 'SMB';
export type Region = 'North America' | 'EMEA' | 'APAC';
export type Period = '2025-06' | '2025-07' | '2025-08' | '2025-09' | '2025-10';

export interface DemoMetricRecord {
  id: string;
  period: Period;
  segment: Segment;
  region: Region;
  revenue: number;
  conversionRate: number;
  engagementScore: number;
  growthDelta: number;
}

const periods: Period[] = ['2025-06', '2025-07', '2025-08', '2025-09', '2025-10'];
const segments: Segment[] = ['Enterprise', 'Midmarket', 'SMB'];
const regions: Region[] = ['North America', 'EMEA', 'APAC'];

type Seed = Record<Segment, Record<Region, number[]>>;

const revenueSeed: Seed = {
  Enterprise: {
    'North America': [780_000, 805_000, 842_000, 865_000, 910_000],
    EMEA: [520_000, 535_000, 556_000, 590_000, 615_000],
    APAC: [460_000, 472_000, 488_000, 505_000, 538_000]
  },
  Midmarket: {
    'North America': [320_000, 334_000, 352_000, 370_000, 385_000],
    EMEA: [210_000, 220_000, 232_000, 246_000, 259_000],
    APAC: [190_000, 198_000, 206_000, 214_000, 226_000]
  },
  SMB: {
    'North America': [150_000, 158_000, 166_000, 172_000, 184_000],
    EMEA: [120_000, 126_000, 132_000, 138_000, 144_000],
    APAC: [110_000, 114_000, 118_000, 122_000, 128_000]
  }
};

const conversionSeed: Seed = {
  Enterprise: {
    'North America': [0.082, 0.085, 0.087, 0.089, 0.092],
    EMEA: [0.073, 0.074, 0.076, 0.079, 0.081],
    APAC: [0.068, 0.069, 0.071, 0.073, 0.075]
  },
  Midmarket: {
    'North America': [0.062, 0.063, 0.065, 0.066, 0.068],
    EMEA: [0.054, 0.055, 0.056, 0.058, 0.06],
    APAC: [0.048, 0.05, 0.051, 0.052, 0.054]
  },
  SMB: {
    'North America': [0.038, 0.04, 0.041, 0.042, 0.043],
    EMEA: [0.034, 0.035, 0.036, 0.037, 0.039],
    APAC: [0.03, 0.031, 0.032, 0.033, 0.034]
  }
};

const engagementSeed: Seed = {
  Enterprise: {
    'North America': [78, 80, 82, 84, 86],
    EMEA: [74, 75, 76, 78, 79],
    APAC: [72, 73, 74, 76, 77]
  },
  Midmarket: {
    'North America': [66, 67, 68, 70, 71],
    EMEA: [63, 64, 65, 66, 67],
    APAC: [60, 61, 62, 63, 64]
  },
  SMB: {
    'North America': [58, 59, 60, 61, 62],
    EMEA: [55, 56, 57, 58, 59],
    APAC: [52, 53, 54, 55, 56]
  }
};

const growthSeed: Seed = {
  Enterprise: {
    'North America': [0.045, 0.043, 0.047, 0.049, 0.052],
    EMEA: [0.036, 0.038, 0.04, 0.042, 0.044],
    APAC: [0.04, 0.041, 0.043, 0.045, 0.047]
  },
  Midmarket: {
    'North America': [0.032, 0.033, 0.034, 0.035, 0.036],
    EMEA: [0.028, 0.029, 0.03, 0.031, 0.032],
    APAC: [0.027, 0.028, 0.029, 0.03, 0.031]
  },
  SMB: {
    'North America': [0.024, 0.025, 0.026, 0.027, 0.028],
    EMEA: [0.022, 0.023, 0.024, 0.025, 0.026],
    APAC: [0.021, 0.022, 0.023, 0.024, 0.025]
  }
};

const makeRecords = () => {
  const records: DemoMetricRecord[] = [];
  periods.forEach((period, periodIndex) => {
    segments.forEach((segment) => {
      regions.forEach((region) => {
        records.push({
          id: `${period}-${segment}-${region}`,
          period,
          segment,
          region,
          revenue: revenueSeed[segment][region][periodIndex],
          conversionRate: conversionSeed[segment][region][periodIndex],
          engagementScore: engagementSeed[segment][region][periodIndex],
          growthDelta: growthSeed[segment][region][periodIndex]
        });
      });
    });
  });
  return records;
};

export const demoMetrics: DemoMetricRecord[] = makeRecords();

export interface DashboardFilters {
  periodRange: [Period, Period];
  segment: Segment | 'All';
  region: Region | 'All';
}

const defaultPeriodRange: [Period, Period] = [periods[periods.length - 3], periods[periods.length - 1]];

export const defaultFilters: DashboardFilters = {
  periodRange: defaultPeriodRange,
  segment: 'All',
  region: 'All'
};

export const getAvailablePeriods = () => [...periods];
export const getAvailableSegments = () => ['All', ...segments] as const;
export const getAvailableRegions = () => ['All', ...regions] as const;

const withinPeriodRange = (period: Period, [start, end]: [Period, Period]) => {
  const startIndex = periods.indexOf(start);
  const endIndex = periods.indexOf(end);
  const periodIndex = periods.indexOf(period);
  return periodIndex >= startIndex && periodIndex <= endIndex;
};

const matchesSegment = (segment: Segment, applied: Segment | 'All') => applied === 'All' || applied === segment;
const matchesRegion = (region: Region, applied: Region | 'All') => applied === 'All' || applied === region;

export const filterMetrics = (filters: DashboardFilters) =>
  demoMetrics.filter(
    (record) =>
      withinPeriodRange(record.period, filters.periodRange) &&
      matchesSegment(record.segment, filters.segment) &&
      matchesRegion(record.region, filters.region)
  );

export interface KpiSummary {
  totalRevenue: number;
  averageConversionRate: number;
  averageEngagement: number;
  averageGrowthDelta: number;
}

export const computeKpiSummary = (records: DemoMetricRecord[]): KpiSummary => {
  if (records.length === 0) {
    return {
      totalRevenue: 0,
      averageConversionRate: 0,
      averageEngagement: 0,
      averageGrowthDelta: 0
    };
  }

  const totals = records.reduce(
    (agg, record) => {
      agg.revenue += record.revenue;
      agg.conversion += record.conversionRate;
      agg.engagement += record.engagementScore;
      agg.growth += record.growthDelta;
      return agg;
    },
    { revenue: 0, conversion: 0, engagement: 0, growth: 0 }
  );

  return {
    totalRevenue: totals.revenue,
    averageConversionRate: totals.conversion / records.length,
    averageEngagement: totals.engagement / records.length,
    averageGrowthDelta: totals.growth / records.length
  };
};

export interface TrendSeriesPoint {
  period: Period;
  revenue: number;
  conversionRate: number;
  engagementScore: number;
}

export const buildTrendSeries = (records: DemoMetricRecord[]): TrendSeriesPoint[] => {
  const grouped = new Map<Period, { revenue: number; conversion: number; engagement: number; count: number }>();

  records.forEach((record) => {
    const entry = grouped.get(record.period) ?? { revenue: 0, conversion: 0, engagement: 0, count: 0 };
    entry.revenue += record.revenue;
    entry.conversion += record.conversionRate;
    entry.engagement += record.engagementScore;
    entry.count += 1;
    grouped.set(record.period, entry);
  });

  return periods
    .filter((period) => grouped.has(period))
    .map((period) => {
      const entry = grouped.get(period)!;
      return {
        period,
        revenue: entry.revenue,
        conversionRate: entry.conversion / entry.count,
        engagementScore: entry.engagement / entry.count
      };
    });
};

export interface GroupedMetric {
  label: string;
  value: number;
}

export const buildSegmentBreakdown = (records: DemoMetricRecord[]): GroupedMetric[] => {
  const sums = new Map<Segment, number>();
  records.forEach((record) => {
    sums.set(record.segment, (sums.get(record.segment) ?? 0) + record.revenue);
  });
  return Array.from(sums.entries()).map(([segment, value]) => ({ label: segment, value }));
};

export const buildRegionShare = (records: DemoMetricRecord[]): GroupedMetric[] => {
  const sums = new Map<Region, number>();
  records.forEach((record) => {
    sums.set(record.region, (sums.get(record.region) ?? 0) + record.revenue);
  });
  return Array.from(sums.entries()).map(([region, value]) => ({ label: region, value }));
};

export interface InsightCandidate {
  id: string;
  record: DemoMetricRecord;
}

export const getInsightCandidates = (records: DemoMetricRecord[]): InsightCandidate[] =>
  records
    .filter((record) => record.growthDelta >= 0.04)
    .map((record) => ({ id: record.id, record }))
    .sort((a, b) => b.record.growthDelta - a.record.growthDelta);
EOF"],"workdir":"/Users/ivan/Project/EPB"}

