{"command":["bash","-lc","cat <<'EOF' > src/dashboard/hooks/useDashboardState.tsx
import {
  type DashboardFilters,
  type DemoMetricRecord,
  defaultFilters,
  filterMetrics,
  computeKpiSummary,
  buildTrendSeries,
  buildSegmentBreakdown,
  buildRegionShare,
  getInsightCandidates,
  getAvailablePeriods,
  getAvailableSegments,
  getAvailableRegions
} from '../data/metrics';
import { createContext, useContext, useMemo, useState, type ReactNode } from 'react';

interface DashboardStateContextValue {
  filters: DashboardFilters;
  setFilters: (updater: Partial<DashboardFilters>) => void;
  records: DemoMetricRecord[];
  kpis: ReturnType<typeof computeKpiSummary>;
  trendSeries: ReturnType<typeof buildTrendSeries>;
  segmentBreakdown: ReturnType<typeof buildSegmentBreakdown>;
  regionShare: ReturnType<typeof buildRegionShare>;
  insightCandidates: ReturnType<typeof getInsightCandidates>;
  options: {
    periods: ReturnType<typeof getAvailablePeriods>;
    segments: ReadonlyArray<string>;
    regions: ReadonlyArray<string>;
  };
  isEmpty: boolean;
}

const DashboardStateContext = createContext<DashboardStateContextValue | null>(null);

interface DashboardStateProviderProps {
  children: ReactNode;
}

export const DashboardStateProvider = ({ children }: DashboardStateProviderProps) => {
  const [filters, setLocalFilters] = useState<DashboardFilters>(defaultFilters);

  const records = useMemo(() => filterMetrics(filters), [filters]);
  const kpis = useMemo(() => computeKpiSummary(records), [records]);
  const trendSeries = useMemo(() => buildTrendSeries(records), [records]);
  const segmentBreakdown = useMemo(() => buildSegmentBreakdown(records), [records]);
  const regionShare = useMemo(() => buildRegionShare(records), [records]);
  const insightCandidates = useMemo(() => getInsightCandidates(records), [records]);

  const setFilters = (updater: Partial<DashboardFilters>) => {
    setLocalFilters((prev) => ({
      periodRange: updater.periodRange ?? prev.periodRange,
      segment: updater.segment ?? prev.segment,
      region: updater.region ?? prev.region
    }));
  };

  const value: DashboardStateContextValue = {
    filters,
    setFilters,
    records,
    kpis,
    trendSeries,
    segmentBreakdown,
    regionShare,
    insightCandidates,
    options: {
      periods: getAvailablePeriods(),
      segments: getAvailableSegments(),
      regions: getAvailableRegions()
    },
    isEmpty: records.length === 0
  };

  return <DashboardStateContext.Provider value={value}>{children}</DashboardStateContext.Provider>;
};

export const useDashboardState = () => {
  const context = useContext(DashboardStateContext);
  if (!context) {
    throw new Error('useDashboardState must be used within DashboardStateProvider');
  }
  return context;
};
EOF"]}

