import { useDashboardState } from '../hooks/useDashboardState';
import { useFormatters } from '../hooks/useFormatters';
import { GlowCard, ResponsiveGrid } from './layout';

export const KpiTiles = () => {
  const { kpis, filters } = useDashboardState();
  const { formatCurrency, formatPercent, formatNumber } = useFormatters();

  const periodLabel = `${filters.periodRange[0]} to ${filters.periodRange[1]}`;

  return (
    <ResponsiveGrid>
      <GlowCard
        label="Total Revenue"
        value={formatCurrency(kpis.totalRevenue)}
        delta={{
          trend: 'up',
          value: kpis.averageGrowthDelta,
