import { useDashboardState } from '../hooks/useDashboardState';
import { useFormatters } from '../hooks/useFormatters';
import { ChartCard, EmptyState, palette, theme } from './ChartTheme';

export const RegionPieChart = () => {
  const { regionShare, isEmpty } = useDashboardState();
  const { formatCurrency, formatPercent } = useFormatters();

  const pieData = useMemo(() => {
    const total = regionShare.reduce((sum, entry) => sum + entry.value, 0);
    return regionShare.map((entry, index) => ({
      id: entry.label,
      label: entry.label,
      value: entry.value,
      color: palette.primary[index % palette.primary.length],
      share: total === 0 ? 0 : entry.value / total
    }));
  }, [regionShare]);

  return (
    <ChartCard
      title="Regional Contribution"
      subtitle="Share of total revenue by operating region"
      footer="Hover to reveal proportional contribution with absolute revenue."
