{"command":["bash","-lc","cat <<'EOF' > src/dashboard/components/FilterPanel.tsx
import { useMemo } from 'react';
import { DashboardSection } from './layout';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { useDashboardState } from '../hooks/useDashboardState';

interface PeriodOption {
  value: string;
  label: string;
  range: [string, string];
}

export const FilterPanel = () => {
  const { filters, setFilters, options } = useDashboardState();

  const periodOptions = useMemo<PeriodOption[]>(() => {
    const periods = options.periods;
    if (!periods.length) {
      return [];
    }

    const latestThree: PeriodOption = {
      value: 'latest-3',
      label: `Latest 3 periods (${periods[periods.length - 3]} to ${periods[periods.length - 1]})`,
      range: [periods[Math.max(0, periods.length - 3)], periods[periods.length - 1]]
    };

    const fullWindow: PeriodOption = {
      value: 'full-window',
      label: `Full window (${periods[0]} to ${periods[periods.length - 1]})`,
      range: [periods[0], periods[periods.length - 1]]
    };

    const singlePeriods = periods.map<PeriodOption>((period) => ({
      value: `single-${period}`,
      label: `Focus on ${period}`,
      range: [period, period]
    }));

    return [latestThree, fullWindow, ...singlePeriods];
  }, [options.periods]);

  const selectedPeriod = periodOptions.find((option) => option.range[0] === filters.periodRange[0] && option.range[1] === filters.periodRange[1]);

  return (
    <DashboardSection
      title="Filters"
      subtitle="Tune the lens across timeframe, segment, and region to remix the narrative"
      className="border border-slate-800/70"
    >
      <div className="grid gap-4 md:grid-cols-3">
        <div className="flex flex-col gap-2">
          <span className="text-xs uppercase tracking-[0.35em] text-slate-400">Timeframe</span>
          <Select
            value={selectedPeriod?.value ?? 'latest-3'}
            onValueChange={(value) => {
              const option = periodOptions.find((item) => item.value === value);
              if (option) {
                setFilters({ periodRange: option.range as [typeof filters.periodRange[0], typeof filters.periodRange[1]] });
              }
            }}
          >
            <SelectTrigger>
              <SelectValue placeholder="Select timeframe" />
            </SelectTrigger>
            <SelectContent>
              {periodOptions.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="flex flex-col gap-2">
          <span className="text-xs uppercase tracking-[0.35em] text-slate-400">Segment</span>
          <Select
            value={filters.segment}
            onValueChange={(value) => setFilters({ segment: value as typeof filters.segment })}
          >
            <SelectTrigger>
              <SelectValue placeholder="Select segment" />
            </SelectTrigger>
            <SelectContent>
              {options.segments.map((segment) => (
                <SelectItem key={segment} value={segment}>
                  {segment}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="flex flex-col gap-2">
          <span className="text-xs uppercase tracking-[0.35em] text-slate-400">Region</span>
          <Select value={filters.region} onValueChange={(value) => setFilters({ region: value as typeof filters.region })}>
            <SelectTrigger>
              <SelectValue placeholder="Select region" />
            </SelectTrigger>
            <SelectContent>
              {options.regions.map((region) => (
                <SelectItem key={region} value={region}>
                  {region}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>
    </DashboardSection>
  );
};
EOF"]}

