+export const parseRecordIdentifier = (identifier: string) => {
+  const parts = identifier.split('-');
+  if (parts.length < 4) {
+    return { period: parts[0] as Period, segment: parts[1] as Segment, region: parts[2] as Region };
+  }
+  const [year, month, segment, ...regionParts] = parts;
+  const period = `${year}-${month}` as Period;
+  const region = regionParts.join('-') as Region;
+  return {
+    period,
+    segment: segment as Segment,
+    region
+  };
+};
